import React, { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import "./OrderSummary.css"; // Tạo file CSS để định kiểu cho OrderSummary

const OrderSummary = () => {
  const { getTotalCartAmount, all_product, cartItems } = useContext(ShopContext);

  const totalItems = all_product.reduce((total, product) => {
    return total + (cartItems[product.id] || 0);
  }, 0);

  const handleBuy = () => {
    alert("Purchase complete!");
  };

  return (

    <div>
      <div className="order-summary-products">
        {all_product.map((e, idx) => {
          if (cartItems[e.id] > 0) {
            return (
              <div key={idx} className="order-summary-product-item">
                <img src={e.image} alt="" className="product-icon" />
                <p>{e.name}</p>
              </div>
            );
          }
          return null;
        })}
      </div>
      <div className="order-summary">
        <h1>Order Summary</h1>
        <div className="order-summary-details">
          <p>Total Items: {totalItems}</p>
          <p>Shipping & handling: Free</p>
          <p>Total before: ${getTotalCartAmount()}</p>
          <p>Order total: ${getTotalCartAmount()}</p>
        </div>
        <button className="buy-button" onClick={handleBuy}>Buy</button>
      </div>
    </div>
  );
};

export default OrderSummary;
