import React, { useContext, useState } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import './OrderSummary.css';

const OrderSummary = () => {
  const { cartItems, all_product, getTotalCartAmount } = useContext(ShopContext);
  const [ratings, setRatings] = useState({});

  const handleRatingChange = (productId, rating) => {
    setRatings({ ...ratings, [productId]: rating });
  };

  const handleSubmitRatings = () => {
    if (localStorage.getItem('auth-token')) {
      Object.keys(ratings).forEach(productId => {
        fetch('http://localhost:4000/rateProduct', {
          method: 'POST',
          headers: {
            Accept: 'application/json',
            'auth-token': `${localStorage.getItem('auth-token')}`,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ itemId: productId, rating: ratings[productId] }),
        })
        .then(response => response.json())
        .then(data => console.log(data));
      });
    }
    alert('Ratings submitted!');
  };

  return (
    <div className="order-summary">
      <h1>Order Summary</h1>
      <div className="order-summary-details">
        <p>Items: {Object.keys(cartItems).length}</p>
        <p>Shipping & handling: Free</p>
        <p>Total before: ${getTotalCartAmount()}</p>
        <p>Order total: ${getTotalCartAmount()}</p>
      </div>
      <div className="rating-section">
        <h2>Rate Your Products</h2>
        {all_product.map((product, idx) => {
          if (cartItems[product.id] > 0) {
            return (
              <div key={idx} className="rating-item">
                <p>{product.name}</p>
                <input
                  type="number"
                  min="1"
                  max="5"
                  value={ratings[product.id] || ''}
                  onChange={(e) => handleRatingChange(product.id, e.target.value)}
                />
              </div>
            );
          }
          return null;
        })}
        <button onClick={handleSubmitRatings}>Submit Ratings</button>
      </div>
    </div>
  );
};

export default OrderSummary;
