//import React from "react";
import "./RelatedProducts.css";
//import data_product from "../Assets/data";
import Item from "../Item/Item";
import React, { useContext, useEffect, useState } from "react";
// import axios from 'axios';
// import { ShopContext } from '../Context/ShopContext'

const RelatedProducts = () => {
  const[new_collection,setNew_collection] = useState([]);
  // bổ sung thêm const[itemName, userName]
  useEffect(()=>{
    fetch('http://localhost:4000/newcollections') // gọi API
    .then((response)=>response.json()) //chuyển response từ json sang js object
    .then((data)=>setNew_collection(data)); //cập nhật data vừa nhận được
  },[])
  // const [userName] = useContext(ShopContext);

  // useEffect(() => {
  //   const fetchRecommendations = async () => {
  //     try {
  //       const response = await axios.get('http://localhost:4040/recommend', {
  //         params: { item_name: productName, user_name: userName }
  //       });
  //       setNew_collection(response.data);
  //     } catch (error) {
  //       console.error("Error fetching recommendations:", error);
  //     }
  //   };

  //   fetchRecommendations();
  // }, [productName, userName]); // Thêm userName vào mảng dependencies của useEffect

  return (
    <div className="relatedproducts">
      <h1>Related Products</h1>
      <hr/>
      <div className="relatedproducts-item">
        {new_collection.map((item, idx) => {
          return (
            <Item
              key={idx}
              id={item._id}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price}
            />
          );
        })}
      </div>
    </div>
  );
};

export default RelatedProducts;
