//import React from "react";
import "./RelatedProducts.css";
//import data_product from "../Assets/data";
import Item from "../Item/Item";
import React, { useEffect, useState } from "react";
import axios from 'axios';
const RelatedProducts = () => {
  // const[new_collection,setNew_collection] = useState([]);
  // useEffect(()=>{
  //   fetch('http://localhost:4000/newcollections') // gọi API
  //   .then((response)=>response.json()) //chuyển response từ json sang js object
  //   .then((data)=>setNew_collection(data)); //cập nhật data vừa nhận được
  // },[])
  // Fetch đề xuất món ăn từ API
  const fetchRecommendations = async (itemName, userName) => {
    try {
        const response = await axios.get('http://localhost:4040/recommend/', {
            params: {
                item_name: itemName,
                user_name: userName
            }
        });

        if (response.status === 200) {
            setRecommendations(response.data.recommendations);
            fetchItem(response.data.recommendations)
        } else {
            console.log("Error fetching recommendations");
        }
    } catch (error) {
        console.log("Error:", error);
    }
  };
  // Fetch thông tin các món ăn đề xuất từ API
  const fetchItem = async (list) => {
    const item = []
    for (let i = 0; i < list.length; i++) {
        const response = await axios.get(`http://localhost:8000/recommend/`)
        if (response.status === 200) {
            item.push(response.data.data)
        } else {
            console.log("Can not find item")
        }
    }
    setFullItem(item)
  }
  return (
    <div className="relatedproducts">
      <h1>Related Products</h1>
      <hr/>
      <div className="relatedproducts-item">
        {new_collection.map((item, idx) => {
          return (
            <Item
              key={idx}
              id={item.id}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price}
            />
          );
        })}
      </div>
    </div>
  );
};

export default RelatedProducts;
