import React, { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";
import "./OrderSummary.css"; // Tạo file CSS để định kiểu cho OrderSummary

const OrderSummary = () => {
  const { getTotalCartAmount, all_product, cartItems, removeFromCart } = useContext(ShopContext);

  return (
    <div className="order-summary-container">
      <div className="order-summary-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      <div className="order-summary-products">
        {all_product.map((e, idx) => {
          if (cartItems[e.id] > 0) {
            return (
              <div key={idx} className="order-summary-format order-summary-format-main">
                <img src={e.image} alt="" className="product-icon" />
                <p>{e.name}</p>
                <p>${e.new_price}</p>
                <button className="order-summary-quantity">{cartItems[e.id]}</button>
                <p>${e.new_price * cartItems[e.id]}</p>
                <button
                  className="order-summary-remove-icon"
                  onClick={() => {
                    removeFromCart(e.id);
                  }}
                >
                  Remove
                </button>
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
};

export default OrderSummary;
