import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useHistory } from 'react-router-dom';
import './Rating.css'; // Thêm tệp CSS

const Rating = () => {
    const location = useLocation();
    const history = useHistory();
    const { purchasedItems } = location.state || { purchasedItems: [] };
    const [ratings, setRatings] = useState({});

    const handleRatingChange = (productId, value) => {
        setRatings({ ...ratings, [productId]: value });
    };

    const handleSubmit = async () => {
        try {
            await Promise.all(purchasedItems.map(item =>
                axios.post('/addrating', { productId: item.id, rating: ratings[item.id] })
            ));
            history.push('/thankyou');
        } catch (error) {
            console.error("Error submitting ratings:", error);
        }
    };

    return (
        <div className="rating-page">
            <h1>Rate Your Purchased Items</h1>
            {purchasedItems.map(item => (
                <div key={item.id} className="rating-item">
                    <p>{item.name}</p>
                    <input
                        type="number"
                        min="1"
                        max="5"
                        value={ratings[item.id] || ''}
                        onChange={(e) => handleRatingChange(item.id, e.target.value)}
                    />
                </div>
            ))}
            <button onClick={handleSubmit}>Submit Ratings</button>
        </div>
    );
};

export default Rating;
