import React, { useContext } from "react";
import { ShopContext } from "../../Context/ShopContext";

const OrderSummary = () => {
  const { getTotalCartAmount, all_product, cartItems } = useContext(ShopContext);

  const totalItems = all_product.reduce((total, product) => {
    return total + (cartItems[product.id] || 0);
  }, 0);

  const handleBuy = () => {
    alert("Purchase complete!");
  };

  return (
    <div className="order-summary">
      <h1>Order Summary</h1>
      <div>
        {/*<p>Total Items: {totalItems}</p>
        <p>Shipping & handling: Free</p>
        <p>Total before: ${getTotalCartAmount()}</p>
  <p>Order total: ${getTotalCartAmount()}</p>*/}
         <img src={e.image} alt="" className="carticon-product-icon" />
                <p>{e.name}</p>
                <p>${e.new_price}</p>
                <button className="cartitems-quantity">
                  {cartItems[e.id]}
                </button>
                <p>${e.new_price * cartItems[e.id]}</p>
                <img/>
        <button onClick={handleBuy}>Buy</button>
      </div>
    </div>
  );
};

export default OrderSummary;
